import os
target_dir = 'plugins/computer_use'
exclude_dirs = {'chats', 'temp', 'venv', '__pycache__', 'env', 'sandbox', 'node_modules', '.venv', '.git', 'libs'}
result_output = []

if os.path.exists(target_dir):
    for root, dirs, files in os.walk(target_dir):
        dirs[:] = [d for d in dirs if d not in exclude_dirs]
        for file in files:
            if file.endswith(('.pyc', '.pyo', '.exe', '.dll', '.so', '.dylib')) or file == '.DS_Store':
                continue
            abs_path = os.path.join(root, file)
            rel_path = os.path.relpath(abs_path, target_dir)
            try:
                with open(abs_path, 'r', encoding='utf-8', errors='ignore') as f:
                    content = f.read()
                result_output.append(f"\n--- {rel_path} ---\n{content}\n")
            except:
                pass
    result = "".join(result_output)
else:
    result = f"–î–∏—Ä–µ–∫—Ç–æ—Ä–∏—è {target_dir} –Ω–µ –Ω–∞–π–¥–µ–Ω–∞."

